package _01_type;

public class Demo2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int var1 = 10;
		int var2 = 4;
		
		System.out.println(var1 + var2);
		System.out.println(var1 - var2);
		System.out.println(var1 * var2);
		System.out.println(var1 / (double)var2);
		System.out.println(var1 % var2);
		System.out.println(var1 ^ var2);
	}

}
