package _01_type;

public class TriangleArea {

	/*
	 *  밑변이 10,
	 *  높이가 7 인 삼각형의 
	 *  면적을 출력하세요.
	 *  
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int width = 10;
		int height = 7;
		
		double area = width * height / 2.0;
		
		System.out.println("삼각형의 면적: " + area);
		// 35.0  --> "35.0"
	}

}
